import React from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const actionCards = [
    {
      id: 1,
      title: 'Discover Ideas',
      description: 'Browse thousands of photos for inspiration',
      image: '/images/discover-ideas.jpg',
      action: 'Browse Ideas'
    },
    {
      id: 2,
      title: 'Browse Pros',
      description: 'Find and connect with home professionals',
      image: '/images/browse-pros.jpg',
      action: 'Find Professionals'
    },
    {
      id: 3,
      title: 'Suggest Pros for Me',
      description: 'Get personalized professional recommendations',
      image: '/images/suggest-pros.jpg',
      action: 'Get Suggestions'
    }
  ];

  return (
    <section className="hero-section">
      <div className="container">
        <h1 className="hero-title">Here's what you can do on Houzz</h1>
        
        <div className="action-cards">
          {actionCards.map(card => (
            <div key={card.id} className="action-card card">
              <div className="card-image">
                <img src={card.image} alt={card.title} />
              </div>
              <div className="card-content">
                <h3 className="card-title">{card.title}</h3>
                <p className="card-description">{card.description}</p>
                <button className="btn btn-primary card-btn">
                  {card.action}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
